googleAuthApp.service("adminHomeService",function($http,$mdDialog){var self=this;self.adminObject={},self.postAdmins=function(adminFirst,adminLast,adminEmail){return self.getAdmins(),$http({method:"POST",url:"/private/postAdmins",data:{firstname:adminFirst,lastname:adminLast,email:adminEmail,active:!0}}).then(function(response){self.clearForm()})},self.getAdmins=function(){return $http({method:"GET",url:"/private/getAdmins"}).then(function(response){self.adminObject.admins=response.data,self.clearForm()})},self.deleteAdmins=function(ev,id){var confirm=$mdDialog.confirm().title("Would you like to delete this user from the system?").ariaLabel("Delete admin").targetEvent(ev).ok("Delete Admin User").cancel("Cancel");$mdDialog.show(confirm).then(function(){self.status="Admin User is deleted",$http({method:"DELETE",url:"/private/deleteAdmins",params:{id:id}}).then(function(response){self.getAdmins()})},function(){self.status="user was not deleted, Thank you."})},self.clearForm=function(){clearForm1.reset(),clearForm2.reset(),clearForm3.reset()}});